<script>
import Detail from './Detail'
import lineCharts from './lineChart'
import barCharts from './barCharts'
import progressCharts from './progressCharts'
import { mapGetters } from 'vuex'
export default {
  name: '',
  components: {
    Detail,
    lineCharts,
    barCharts,
    progressCharts
  },
  computed: {
    ...mapGetters(['supplyAndDemand']),
    demandCount() {
      let sum = 0
      if (this.supplyAndDemand) {
        this.supplyAndDemand.demand.value.forEach(element => {
          sum += element
        })
      }
      return sum
    },
    supplyCount() {
      let sum = 0
      if (this.supplyAndDemand) {
        this.supplyAndDemand.supply.value.forEach(element => {
          sum += element
        })
      }
      return sum
    }
  }
}
